:root {
  --radius: 0.75rem;

  /* Warm off-white base */
  --background: oklch(0.978 0.004 80);
  --foreground: oklch(0.18 0.015 260);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.18 0.015 260);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.18 0.015 260);

  /* Deep indigo primary */
  --primary: oklch(0.42 0.2 264);
  --primary-foreground: oklch(0.98 0 0);
  --secondary: oklch(0.96 0.006 264);
  --secondary-foreground: oklch(0.42 0.2 264);
  --muted: oklch(0.95 0.005 80);
  --muted-foreground: oklch(0.52 0.02 264);
  --accent: oklch(0.94 0.012 264);
  --accent-foreground: oklch(0.35 0.18 264);
  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.91 0.008 264);
  --input: oklch(0.93 0.006 264);
  --ring: oklch(0.42 0.2 264 / 0.4);

  /* Charts */
  --chart-1: oklch(0.42 0.2 264);
  --chart-2: oklch(0.58 0.18 210);
  --chart-3: oklch(0.62 0.16 160);
  --chart-4: oklch(0.72 0.15 85);
  --chart-5: oklch(0.65 0.2 30);

  /* Sidebar — deep navy */
  --sidebar: oklch(0.22 0.04 264);
  --sidebar-foreground: oklch(0.92 0.01 264);
  --sidebar-primary: oklch(0.52 0.22 264);
  --sidebar-primary-foreground: oklch(0.98 0 0);
  --sidebar-accent: oklch(0.30 0.05 264);
  --sidebar-accent-foreground: oklch(0.95 0.01 264);
  --sidebar-border: oklch(0.32 0.04 264);
  --sidebar-ring: oklch(0.52 0.22 264);

  /* Custom tokens */
  --gradient-hero: linear-gradient(135deg, oklch(0.42 0.2 264), oklch(0.38 0.22 280));
  --gradient-card: linear-gradient(160deg, oklch(1 0 0), oklch(0.97 0.008 264));
  --gradient-accent: linear-gradient(135deg, oklch(0.52 0.22 264 / 0.12), oklch(0.58 0.18 210 / 0.08));
  --shadow-card: 0 1px 3px oklch(0.18 0.015 260 / 0.06), 0 4px 16px oklch(0.18 0.015 260 / 0.04);
  --shadow-hero: 0 8px 32px oklch(0.42 0.2 264 / 0.25);
  --transition-smooth: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  --font-sans: 'Plus Jakarta Sans', sans-serif;
}

